import { Component, Input, OnChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-cv-page',
  standalone: true,
  imports: [CommonModule],
  template: `
  <div class="card" *ngIf="loading">Loading…</div>
  <ng-container *ngIf="!loading">
    <div class="card">
      <h1>{{ data?.header?.name }}</h1>
      <div class="small">{{ data?.header?.title }}</div>
      <div class="kv" style="margin-top:12px">
        <div>Email</div><div><a [href]="'mailto:'+data?.header?.email">{{ data?.header?.email }}</a></div>
        <div>Phone</div><div><a [href]="'tel:'+data?.header?.phone">{{ data?.header?.phone }}</a></div>
        <div>LinkedIn</div><div><a [href]="data?.header?.linkedin" target="_blank" rel="noopener">{{ data?.header?.linkedin }}</a></div>
        <div>Location</div><div>{{ data?.header?.location }}</div>
      </div>
    </div>

    <div class="card" *ngIf="data?.summary?.length">
      <h2>Summary</h2>
      <p *ngFor="let p of data?.summary">{{ p }}</p>
    </div>
  </ng-container>
  `
})
export class CvPageComponent implements OnChanges {
  @Input() lang: 'fr'|'en' = 'fr';
  @Input() variant: 'full'|'short' = 'full';
  data: any = null;
  loading = true;

  constructor(private http: HttpClient) {}

  ngOnChanges(): void {
    this.loading = true;
    const path = this.variant === 'short'
      ? `assets/content/${this.lang}/short.json`
      : `assets/content/${this.lang}/full.json`;
    this.http.get(path).subscribe({
      next: (d) => { this.data = d; this.loading = false; },
      error: () => { this.data = null; this.loading = false; }
    });
  }
}
